function(){var result=(doc.getItemValueString('s').isEmpty()) ? '' : doc.getItemValue('s').stream().map(function(corr) {
var res='';
var corrs = corr.split(';');
for (var i=0;i<corrs.length;i++){
 if(i>0) {
  res+=', ';
 }
 var curs = corrs[i].split('%');
 if (curs.length==1){
  /* системная организация без адресатов*/
  var ind = doc.getItemValue('s').indexOf(corr);
  var orgId = doc.getItemValue('FirmId').get(ind);
  var orgRepId = doc.getItemValue('OrgSoRepId').get(ind);
  var cmjunid = orgId+orgRepId;
  var beardColl = session.find('select id from so_beard where cmjunid=\''+cmjunid+'\'');
  if (!beardColl.isEmpty()) {
    var orgBeardObj = session.find(beardColl.get(0));
    if (orgBeardObj!=null) {
        res+=orgBeardObj.get('orig_shortName');
    }
  }
 } else {
  /* несистемная организация без адресатов либо сотрудники системной или несистемной организации*/
  res+=(curs[0]=='$')?curs[1]:curs[0];
 }
}
return res;
}).collect(java.util.stream.Collectors.joining(', '));
return result.split(', ').length>4?java.util.Arrays.asList(result.split(', ')).stream().limit(4).collect(java.util.stream.Collectors.joining(', ')).concat(', ...'):result;
}.call()
